function sendTextToClipboard(){var e=document.querySelector(".clipboard .select-item.id").value,o=document.querySelector(".clipboard textarea").value;fetch(`https://apis.cancin.cn/clipboard?mode=send&id=${e}&content=${encodeURIComponent(o)}`).then(e=>e.json()).then(e=>{200==e.code?set_notice.checked&&tools.showMessage(e.message,"success",2):(console.log(e.message),set_notice.checked&&tools.showMessage(e.message,"error",2))}).catch(e=>console.error(e))}function getTextFromClipboard(){var e=document.querySelector(".clipboard .select-item.id").value,o=document.querySelector(".clipboard textarea");fetch(`https://apis.cancin.cn/clipboard?mode=get&id=${e}&content=`).then(e=>e.json()).then(e=>{200==e.code?(o.value=e.content[0].content,set_notice.checked&&tools.showMessage(e.message,"success",2)):(console.log(e.message),set_notice.checked&&tools.showMessage(e.message,"error",2))}).catch(e=>console.error(e))}